ass="row">                <div class="col-xs-10">                    <?= Html::textInput('r', $r, [                        'class' => 'form-control',                        'placeholder' => Yii::t('app', 'enter petroglyph name'),                    ]) ?>                </div>                <div class="col-xs-2">                    <?= Html::submitButton(Yii::t('app', 'Search'), ['class' => 'btn pull-right']) ?>                </div>            </div>        </div>        <?php ActiveForm::end(); ?>    </div>    <div class="col-xs-12 col-md-8">        <?php if ($r): ?>            <div class="form-control alert-info text-center">                <?php if ($json_petroglyphs == '[]'): ?>                    <?= Yii::t('app', 'Nothing found') ?>                <?php else: ?>                    <?= Yii::t('app', 'The number of found petroglyphs: {n}', ['n' => count(json_decode($json_petroglyphs))]) ?>                <?php endif; ?>            </div>        <?php endif; ?>    </div></div><div id="map_canvas" style="width:100%; height:600px; float:left; margin-right: 20px;"></div><div id="search_container">    <form method="post">        <!--        <input type="text" name="name" placeholder="--><? //=lang("title");?><!--...">-->        <!--        <button type="submit" name="filter" class="btn btn-default">-->        <? //=lang("filter");?><!--</button>-->    </form></div>